import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendar, faClock } from '@fortawesome/free-solid-svg-icons';
import Layout from "../../components/layout/Layout";
import Header from "../../components/header/Header";
import { useDataContext } from '../../services/DataContext';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import "./createService.css"

function CreateService () {
    const { createEducationalService } = useDataContext();

    const schema = yup.object().shape({
        titulo: yup.string().required('O título é obrigatório'),
        dataServico: yup.string().required('A data do serviço é obrigatória'),
        horaInicio: yup.string().required('A hora de início é obrigatória'),
        horaFinal: yup.string().required('A hora final é obrigatória'),
        FKservico: yup.string().required('O tipo de serviço é obrigatório'),
        descricao: yup.string().required('A descrição é obrigatória'),
    });

    const { register, handleSubmit, formState: { errors } } = useForm({
        resolver: yupResolver(schema) // Utilizando o resolver do Yup
    });

    // Função para lidar com o envio do formulário
    const onSubmit = async (formData) => {
        createEducationalService(formData)
    };

    /*const [formData, setFormData] = useState({
            dataServico: "",
            horaInicio: "",
            horaFinal: "",
            titulo: "",
            descricao: "",
            FKservico: ""
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
        console.log(formData)
    };
    
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        createEducationalService(formData)
      };*/

    return (
        <Layout>
            <Header title="Adicionar Serviço Educacional" description="Preencha os dados e adicione seu registro de serviço educacional"/>
            <main>
                <div className="createServiceForm-container">
                    <div className="createServiceForm-header">
                        <p>Insira as informações da atividade realizada</p>
                        <hr />
                    </div>
                    <div className="createServiceForm-body">
                    <form onSubmit={handleSubmit}>
                    <div>
                            <label>Título:</label>
                            <input
                            type="text"
                            name="titulo"
                            className="textInput"
                            placeholder="Insira o Título do Serviço Educacional"
                            {...register('titulo')}
                            />
                            {errors.titulo && <span>{errors.titulo.message}</span>}
                        </div>
                        <div className="dateInput-container">
                        <label htmlFor=""><FontAwesomeIcon icon={faCalendar} className="icon"/>Data do Serviço:</label>
                            <input
                            type="date"
                            name="dataServico"
                            {...register('dataServico')}
                            />
                            {errors.dataServico && <span>{errors.dataServico.message}</span>}
                        </div>

                        <div className="timeInput-container">
                                <div className="timeInput-box">
                                    <label htmlFor=""><FontAwesomeIcon icon={faClock} className="icon"/>Hora de Início:</label>
                                    <input
                                    type="time"
                                    name="horaInicio"
                                    {...register('horaInicio')}
                                    />
                                    {errors.horaInicio && <span>{errors.horaInicio.message}</span>}
                                </div>
                                <div className="timeInput-box">
                                    <label htmlFor=""><FontAwesomeIcon icon={faClock} className="icon"/>Hora Final:</label>
                                    <input
                                    type="time"
                                    name="horaFinal"
                                    {...register('horaFinal')}
                                    />
                                    {errors.horaFinal && <span>{errors.horaFinal.message}</span>}
                                </div>
                            </div>                       
                        <div>
                            <label>Tipo de Serviço:</label>
                                <select name="FKservico" {...register('FKservico')}>
                                <option value="">Escolha a atividade </option>
                                <option value="1">Consultoria</option>
                                <option value="2">Monitoria</option>
                                <option value="3">Aula</option>
                            </select>
                            {errors.FKservico && <span>{errors.FKservico.message}</span>}
                        </div>
                        
                        <div>
                            <label>Descrição:</label>
                            <textarea
                            name="descricao"
                            className="BigInput"
                            placeholder="Insira a Descrição do Serviço Educacional"
                            {...register('descricao')}
                            />
                            {errors.descricao && <span>{errors.descricao.message}</span>}

                        </div>
                        <button type="submit" className="main-btn medium">Enviar</button>
                        </form>
                    </div>
                </div>
            </main>
        </Layout>
    )
}

export default CreateService;

