import React from 'react';
import { faTrash, faPenToSquare, faCircleInfo } from '@fortawesome/free-solid-svg-icons'
import { useDataContext } from '../../services/DataContext';
import { Link } from 'react-router-dom';
import TableSituation from "./TableSituation"
import ActionButton from "./ActionButton"
import "./table.css"

const Table = () => {
    const { data, deleteService } = useDataContext();

    const handleDelete = (id) => {
        if (window.confirm('Tem certeza que deseja excluir este serviço?')) {
           deleteService(id);
        }
    };

    const formatarData = (data) => {
        if (!data) return ""; // Verifica se a data está vazia
        const [ano, mes, dia] = data.split("-"); // Divide a data em partes
        return `${dia.padStart(2, "0")}/${mes.padStart(2, "0")}/${ano}`; // Formata a data
    };
    
    const dataFormatada = formatarData(data.dataServico);


    return (
        <div className="table-container">
         
            <table className='table'>
            <thead>
                <tr>
                    <th><input type="checkbox" name="" id="" /></th>
                    <th>Título</th>
                    <th>Data</th>
                    <th>Início</th>
                    <th>Fim</th>
                    <th>Tipo</th>
                    <th>Situação</th>
                    <th colSpan="3">Ações</th>
                </tr>
            </thead>
                    <tbody>
                        { data ? (
                        data.map(registro => (
                        <tr key={registro.id}>
                            <td><input type="checkbox" name="" id={registro.id} /></td>
                            <td><Link to={`/viewServices/${registro.id}`}>{registro.titulo}</Link></td>
                            <td>{dataFormatada}</td>
                            <td>{registro.horaInicio}</td>
                            <td>{registro.horaFinal}</td>
                            <td>{registro.Servico ? registro.Servico.nome : 'N/A'}</td>
                            <td><TableSituation title={registro.status} /></td>
                            <td onClick={() => handleDelete(registro.id)}><ActionButton icon={faTrash} /></td>
                            <td><ActionButton icon={faPenToSquare} url={`/editService/${registro.id}`}/></td>
                            <td><ActionButton icon={faCircleInfo} url={`/viewServices/${registro.id}`} /></td>
                        </tr>
                        ))) : 
                        null
                        
                        }
                    </tbody>
                    </table>
        </div>
    )
}

export default Table;

